INSERT INTO articles (title, author, excerpt, image_url, tags, topics, url, body_text)
VALUES
  ('The Rise of BAYC: From 0.08 ETH to Cultural Phenomenon', 'CryptoVision', 'How 10,000 cartoon apes became the most valuable NFT collection in history and spawned a billion-dollar ecosystem.', 'https://picsum.photos/seed/bayc-article1/800/600', ARRAY['BAYC', 'NFT', 'Web3'], ARRAY['NFT History', 'Digital Art'], 'https://example.com/rise-of-bayc', 'The Bored Ape Yacht Club started as a simple idea: 10,000 unique, programmatically generated apes living on the Ethereum blockchain. What happened next would redefine digital ownership forever...'),
  ('ApeChain Explained: The Entertainment Layer of Web3', 'BlockchainToday', 'Yuga Labs unveils ApeChain, a new Layer 2 blockchain designed specifically for entertainment and gaming applications.', 'https://picsum.photos/seed/bayc-article2/800/600', ARRAY['ApeChain', 'Blockchain', 'Yuga Labs'], ARRAY['Technology', 'Gaming'], 'https://example.com/apechain-explained', 'ApeChain represents Yuga Labs'' vision for a decentralized entertainment future. Built as a Layer 2 solution, it promises faster transactions and lower fees while maintaining the security of Ethereum...'),
